# Functions

def get_token_decimal(decimal):
    decimal = 9 if decimal == 8 else decimal
    decimal = int("1" + str("0" * decimal))
    decimals_dict = {"wei": 1,
                    "kwei": 1000,
                    "babbage": 1000,
                    "femtoether": 1000,
                    "mwei": 1000000,
                    "lovelace": 1000000,
                    "picoether": 1000000,
                    "gwei": 1000000000,
                    "shannon": 1000000000,
                    "nanoether": 1000000000,
                    "nano": 1000000000,
                    "szabo": 1000000000000,
                    "microether": 1000000000000,
                    "micro": 1000000000000,
                    "finney": 1000000000000000,
                    "milliether": 1000000000000000,
                    "milli": 1000000000000000,
                    "ether": 1000000000000000000,
                    "kether": 1000000000000000000000,
                    "grand": 1000000000000000000000,
                    "mether": 1000000000000000000000000,
                    "gether": 1000000000000000000000000000,
                    "tether": 1000000000000000000000000000000}

    key_list = list(decimals_dict.keys())
    val_list = list(decimals_dict.values())

    position = val_list.index(decimal)
    return key_list[position]


def adjust_quantity(number, decimal):
    quantity = int(number)
    decimal_str = str(decimal)
    if '.' in decimal_str:
        decimal *= 10 ** len(decimal_str.split('.')[1])
        quantity += int(decimal)
    return quantity